import React, { useState, useRef, useEffect } from 'react';
import {
  SafeAreaView,
  Text,
  View,
  StyleSheet,
  Animated,
  PanResponder,
  Dimensions
} from 'react-native';

const TARGET_SIZE = 100;
const { width, height } = Dimensions.get('window');

export default function App() {
  const [score, setScore] = useState(0);
  const [color, setColor] = useState('red');
  const [originalColor, setOriginalColor] = useState('red');
  const [hasMoved, setHasMoved] = useState(false); // Track if the square has been moved
  const [elapsedTime, setElapsedTime] = useState(0); // Track elapsed time

  // Dynamically calculate the square size based on the score
  const SQUARE_SIZE = Math.max(30, 60 - score * 5); // Makes the square smaller as the score increases, with a minimum size of 30.

  const pan = useRef(new Animated.ValueXY({ x: 0, y: 0 })).current;
  const timerRef = useRef(null);

  // Function to adjust the shade of the color based on the size of the square
  const adjustColorShade = (color, size) => {
    const hue = getHueFromColor(color);
    const saturation = 70; // Keeping saturation fixed
    const lightness = Math.max(30, Math.min(100, 60 + (SQUARE_SIZE - 30) * 0.5)); // Lighter when bigger, darker when smaller
    return `hsl(${hue}, ${saturation}%, ${lightness}%)`;
  };

  // Extract hue from color (assuming it's a simple color name or hex value)
  const getHueFromColor = (color) => {
    switch(color) {
      case 'red': return 0;
      case 'blue': return 240;
      case 'green': return 120;
      case 'purple': return 270;
      case 'orange': return 30;
      default: return 0;
    }
  };

  // Create a new colored square at a random position along the edge
  const spawnNewSquare = () => {
    const side = Math.floor(Math.random() * 4);
    let startX = 0, startY = 0;

    switch (side) {
      case 0: // top
        startX = Math.random() * (width - SQUARE_SIZE);
        startY = 0;
        break;
      case 1: // right
        startX = width - SQUARE_SIZE;
        startY = Math.random() * (height - SQUARE_SIZE);
        break;
      case 2: // bottom
        startX = Math.random() * (width - SQUARE_SIZE);
        startY = height - SQUARE_SIZE;
        break;
      case 3: // left
        startX = 0;
        startY = Math.random() * (height - SQUARE_SIZE);
        break;
    }

    const colors = ['red', 'blue', 'green', 'purple', 'orange'];
    const newColor = colors[Math.floor(Math.random() * colors.length)];
    setOriginalColor(newColor);  // Store the original color
    setColor(newColor);

    pan.setValue({ x: startX, y: startY });
  };

  useEffect(() => {
    spawnNewSquare();
  }, []);

  const isOverTarget = () => {
    const squareX = pan.x.__getValue();
    const squareY = pan.y.__getValue();

    const targetX = width / 2 - TARGET_SIZE / 2;
    const targetY = height / 2 - TARGET_SIZE / 2;

    return (
      squareX < targetX + TARGET_SIZE &&
      squareX + SQUARE_SIZE > targetX &&
      squareY < targetY + TARGET_SIZE &&
      squareY + SQUARE_SIZE > targetY
    );
  };

  const startTimer = () => {
    if (!hasMoved) {
      setHasMoved(true); // Set hasMoved to true when the square is first moved
      timerRef.current = setInterval(() => {
        setElapsedTime((prevTime) => prevTime + 1); // Increment the time every second
      }, 1000);
    }
  };

  const panResponder = useRef(
    PanResponder.create({
      onStartShouldSetPanResponder: () => true,
      onPanResponderGrant: () => {
        pan.setOffset({
          x: pan.x.__getValue(),
          y: pan.y.__getValue()
        });
        pan.setValue({ x: 0, y: 0 });
        startTimer(); // Start the timer once the square is moved
      },
      onPanResponderMove: Animated.event([null, { dx: pan.x, dy: pan.y }], {
        useNativeDriver: false
      }),
      onPanResponderRelease: () => {
        pan.flattenOffset();
        if (isOverTarget()) {
          setScore(prev => prev + 1);
          spawnNewSquare();
        }
      }
    })
  ).current;

  useEffect(() => {
    // Clean up the timer when the component unmounts or if the game resets
    return () => {
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    };
  }, []);

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.gameContainer}>
        <Text style={styles.scoreText}>Score: {score}</Text>
        <Text style={styles.timerText}>Time: {elapsedTime} seconds</Text> {/* Display the elapsed time */}

        {/* Target Square (Gray) */}
        <View style={styles.targetSquare} />

        {/* Draggable Square (Colored) */}
        <Animated.View
          style={[
            styles.draggableSquare,
            {
              backgroundColor: adjustColorShade(originalColor, SQUARE_SIZE),  // Adjust the color shade based on size
              width: SQUARE_SIZE,
              height: SQUARE_SIZE,
              transform: [
                { translateX: pan.x },
                { translateY: pan.y }
              ]
            }
          ]}
          {...panResponder.panHandlers}
        />

        <Text style={styles.instructions}>Drag the colored square to the gray target</Text>
      </View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f5f5f5'
  },
  gameContainer: {
    flex: 1,
    position: 'relative'
  },
  targetSquare: {
    position: 'absolute',
    width: TARGET_SIZE,
    height: TARGET_SIZE,
    backgroundColor: 'gray',
    left: width / 2 - TARGET_SIZE / 2,
    top: height / 2 - TARGET_SIZE / 2
  },
  draggableSquare: {
    position: 'absolute'
  },
  scoreText: {
    position: 'absolute',
    top: 40,
    left: 20,
    fontSize: 24,
    fontWeight: 'bold'
  },
  timerText: {
    position: 'absolute',
    top: 80,
    left: 20,
    fontSize: 24,
    fontWeight: 'bold'
  },
  instructions: {
    position: 'absolute',
    bottom: 40,
    alignSelf: 'center',
    fontSize: 18,
    textAlign: 'center',
    paddingHorizontal: 20
  }
});
