let box = document.getElementById("box");
let player = document.getElementById("player");
let score = 0;
let timer = 10; // Timer set to 10 seconds
let scoreDisplay = document.getElementById("score");
let timerDisplay = document.getElementById("timer");

let timerInterval;
let scoreResetInterval;

// Function to update the score display
function updateScore() {
    scoreDisplay.textContent = `Score: ${score}`;
}

// Function to handle the timer
function startTimer() {
    timerInterval = setInterval(function() {
        timer--;
        timerDisplay.textContent = `Time Left: ${timer}s`;

        if (timer <= 0) {
            clearInterval(timerInterval);
            resetGame(); // Reset game when timer hits 0
        }
    }, 1000);
}

// Function to reset the score and timer
function resetGame() {
    score = 0;
    timer = 10;
    updateScore();
    startTimer(); // Restart timer
}

// Function to check for collision between the player and the box
function checkCollision() {
    let playerRect = player.getBoundingClientRect();
    let boxRect = box.getBoundingClientRect();

    if (playerRect.left < boxRect.right &&
        playerRect.right > boxRect.left &&
        playerRect.top < boxRect.bottom &&
        playerRect.bottom > boxRect.top) {
        // Collision detected
        score += 10; // Increment score on collision
        updateScore();
        resetTimer(); // Reset timer on collision
    }
}

// Function to reset the timer after a collision
function resetTimer() {
    clearInterval(timerInterval); // Stop the current timer
    timer = 10; // Reset to 10 seconds
    timerDisplay.textContent = `Time Left: ${timer}s`;
    startTimer(); // Restart timer
}

// Event listener to move the player (example using arrow keys)
document.addEventListener("keydown", function(event) {
    let playerStyle = window.getComputedStyle(player);
    let playerTop = parseInt(playerStyle.top);
    let playerLeft = parseInt(playerStyle.left);

    if (event.key === "ArrowUp") {
        player.style.top = (playerTop - 10) + "px";
    } else if (event.key === "ArrowDown") {
        player.style.top = (playerTop + 10) + "px";
    } else if (event.key === "ArrowLeft") {
        player.style.left = (playerLeft - 10) + "px";
    } else if (event.key === "ArrowRight") {
        player.style.left = (playerLeft + 10) + "px";
    }

    checkCollision(); // Check for collision after every move
});

// Initialize the game
function initGame() {
    resetGame();
}

initGame();
